import React, { useEffect, useRef } from 'react';
import { init } from "ityped";
import { FaLocationArrow } from "react-icons/fa6";
import '../styles/Home.css';
import { motion } from 'framer-motion';
import { Container, Typography, Link, Grid, Box, Button, Card, CardMedia, CardContent } from '@mui/material';
import BlueMoon from "../assets/BlueMoon.png";
import TextGenerateEffect from "../utils/TextGenerateEffect";
import Position from "../utils/Position";
import skills from "../assets/Data/skills";

function Home() {
    const textRef = useRef();

  useEffect(() => {
    init(textRef.current, {
      loop: true,
      showCursor: false,
      backSpeed: 150,
      startDelay: 200,
      backDelay: 2000,
      strings: ["Student", "Developer", "Artist", "Wonderer"],
    })
  }, []);

  const path = "M0,100 Q50,50 100,100 Q150,150 200,100";

  return (
    <>
      <section id="home">
        <Container className="moon">
          <motion.img
            className="moon"
            src={BlueMoon}
            alt="moon"
            animate={{ path: path }}
            transition={{ duration: 20, repeat: Infinity }}
          />
        </Container>

        <div className="hero">
          <div className="container">
            <div className="header">
              {/* <p className="title">
                A sense of wonder in a world gone mad
              </p> */}

              <TextGenerateEffect
                words="A Sense of Wonder in a World Gone Mad"
                wordsHighlighted={["Sense", "Wonder", "World"]}
                className="textEffect"
              />

              <Typography variant="h4" className="intro">
                Hi! I&apos;m Tuka, a 
                {/* <span ref={textRef}></span> */}
              </Typography>
              <Container>
                <Typography ref={textRef} variant="h3" className="intro" />
              </Container>
              
              <Button className="button" href="#skills" variant="outlined" color="secondary">
                Show my work <FaLocationArrow />
              </Button>
            </div>
          </div>
        </div>
      </section>

      <section id="skills">
        <Container className="skills">
          <Typography variant="h3">
            What can I do for you?
          </Typography>
          <Box sx={{ flexGrow: 4 }}>
            <Grid container spacing={2} justifyContent="center" alignItems="center">
              <Grid item xs={4}>
                {skills.map(({ id, name, desc, img }) => {
                  return (
                        <Card className="card" key={id}>
                          <CardMedia className="cardimg" image={img} title={name} />
                          <CardContent>
                            <Typography  variant="h5">
                              {name}
                            </Typography>
                            <Typography variant="p">
                              {desc}
                            </Typography>
                          </CardContent>
                        </Card> 
                  )
                })}
              </Grid>
            </Grid>
          </Box>
        </Container>
      </section>
      </>
  );
}

export default Home;
