import React, { useEffect, useRef } from 'react';
import { init } from "ityped";
import { FaLocationArrow } from "react-icons/fa6";
import '../styles/Home.css';
import { motion } from 'framer-motion';
import { Container, Typography, Link, Grid, Box, Button } from '@mui/material';
import BlueMoon from "../assets/BlueMoon.png";
import TextGenerateEffect from "../utils/TextGenerateEffect";



function Home() {
    const textRef = useRef();

  // useEffect(() => {
  //   init(textRef.current, {
  //     loop: true,
  //     showCursor: true,
  //     backSpeed: 100,
  //     startDelay: 120,
  //     backDelay: 1700,
  //     strings: ["Student", "Developer", "Wonderer"],
  //   });
  // }, []);

  const path = "M0,100 Q50,50 100,100 Q150,150 200,100";

  return (
      <section id="home">
        <Container className="moon">
          <motion.img
            className="moon"
            src={BlueMoon}
            alt="moon"
            animate={{ path: path }}
            transition={{ duration: 20, repeat: Infinity }}
          />
        </Container>

        <div className="hero">
          <div className="container">
            <div className="header">
              {/* <p className="title">
                A sense of wonder in a world gone mad
              </p> */}

              <TextGenerateEffect
                words="A Sense of Wonder in a World Gone Mad"
                wordsHighlighted={["Sense", "Wonder", "World"]}
                className="textEffect"
              />
\
              <p className="intro">
                Hi! I&apos;m Tuka, a "STUDENT" based in Canada.
              </p>

              <Button className="button" href="#attributes" variant="outlined" color="secondary">
                Show my work <FaLocationArrow />
              </Button>
            </div>
          </div>
        </div>
      </section>
  );
}

export default Home;
